import React, { useState } from "react";
import { IoIosCloudUpload } from "react-icons/io";

function Main() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [selectedFormat, setSelectedFormat] = useState("");
  const [error, setError] = useState("");

  const FILE_FORMATS = {
    image: {
      accept: "image/*",
      formats: ["PNG", "JPEG", "WEBP", "GIF", "SVG"],
    },
    document: {
      accept: ".doc,.docx,.pdf,.txt,.rtf",
      formats: ["PDF", "DOC", "DOCX", "TXT", "RTF"],
    },
    audio: {
      accept: "audio/*",
      formats: ["MP3", "WAV", "OGG", "M4A"],
    },
    video: {
      accept: "video/*",
      formats: ["MP4", "MOV", "AVI", "WEBM"],
    },
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const fileType = file.type.split('/')[0];
      const validFormats = Object.keys(FILE_FORMATS).map(key => FILE_FORMATS[key].accept);

      if (!validFormats.some(format => 
        format.includes('*') ? file.type.startsWith(format.replace('*', '')) 
                            : format.includes(file.name.split('.').pop().toLowerCase())
      )) {
        setError('Unsupported file format');
        setSelectedFile(null);
        return;
      }

      setSelectedFile(file);
      setError(null);
    }
  };

  const handleConversion = async () => {
    if (!selectedFile || !selectedFormat) {
      setError('Please select a file and output format');
      return;
    }

    try {
      const fileType = selectedFile.type.split('/')[0];
      let convertedFile;

      switch (fileType) {
        case 'image':
          // Handle image conversion
          convertedFile = await convertImage(selectedFile, selectedFormat.toLowerCase());
          break;
        case 'document':
          // Handle document conversion
          convertedFile = await convertDocument(selectedFile, selectedFormat.toLowerCase());
          break;
        case 'audio':
          // Handle audio conversion
          convertedFile = await convertAudio(selectedFile, selectedFormat.toLowerCase());
          break;
        case 'video':
          // Handle video conversion
          convertedFile = await convertVideo(selectedFile, selectedFormat.toLowerCase());
          break;
        default:
          throw new Error('Unsupported file type');
      }

      // Download the converted file
      saveAs(convertedFile, `converted.${selectedFormat.toLowerCase()}`);
    } catch (err) {
      setError('Error converting file: ' + err.message);
    }
  };

  const convertImage = async () => {
    if (!selectedFile || !selectedFormat) {
      setError("Please select both a file and conversion format");
      return;
    }

    try {
      // Create image element
      const image = new Image();
      const reader = new FileReader();

      reader.onload = () => {
        image.src = reader.result;
        image.onload = () => {
          // Create canvas
          const canvas = document.createElement("canvas");
          canvas.width = image.width;
          canvas.height = image.height;
          const ctx = canvas.getContext("2d");
          ctx.drawImage(image, 0, 0);

          // Convert and download
          canvas.toBlob((blob) => {
            const url = URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = url;
            a.download = `converted-image.${selectedFormat}`;
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
            URL.revokeObjectURL(url);
          }, `image/${selectedFormat}`);
        };
      };

      reader.readAsDataURL(selectedFile);
    } catch (err) {
      setError("Error converting image");
      console.error(err);
    }
  };

  return (
    <div>
      <div className="flex text-white">
        <div className="flex-1">
          <div className="p-4">
            <h1 className="text-4xl font-bold">File Type Converter</h1>
          </div>
        </div>
      </div>
      <div className="flex justify-center items-center h-screen">
        <div className="flex flex-col space-y-4 w-full max-w-md px-4">
          <label className="flex flex-col items-center px-4 py-6 bg-neutral-800 text-white rounded-lg cursor-pointer hover:bg-neutral-700 focus:outline-none focus:border-yellow-500 border border-neutral-700">
            <IoIosCloudUpload />
            <span className="mt-2 text-base">
              {selectedFile ? selectedFile.name : "Click to upload file"}
            </span>
            <input
              type="file"
              className="hidden"
              onChange={handleFileChange}
              accept={Object.values(FILE_FORMATS)
                .map((group) => group.accept)
                .join(",")}
            />
          </label>
          {error && <div className="text-red-500 text-sm">{error}</div>}
          <select
            className="w-full p-3 rounded-lg bg-neutral-800 text-white border border-neutral-700 focus:outline-none focus:border-yellow-500"
            value={selectedFormat}
            onChange={(e) => setSelectedFormat(e.target.value)}
          >
            <option value="">Select output format</option>
            {Object.entries(FILE_FORMATS).map(([groupKey, group]) => (
              <optgroup key={groupKey} label={groupKey.toUpperCase()}>
                {group.formats.map((format) => (
                  <option key={format} value={format}>
                    {format}
                  </option>
                ))}
              </optgroup>
            ))}
          </select>
          <button
            className="w-full p-3 rounded-lg bg-yellow-600 text-white font-semibold hover:bg-yellow-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            onClick={convertImage}
            disabled={!selectedFile || !selectedFormat}
          >
            Convert & Download
          </button>
        </div>
      </div>
    </div>
  );
}

export default Main;
